<?phpnamespace Nemundo\Office\Excel\Reader;use Nemundo\Core\Base\DataSource\AbstractRow;use Nemundo\Core\Debug\Debug;use Nemundo\Office\Excel\Date\ExcelDate;class ExcelRow extends AbstractRow{    public function hasValue($name)    {        return parent::hasValue($name);    }    public function getValue($name)    {        return parent::getValue($name);    }    public function hasValueByNumber($number)    {        return parent::hasValueByNumber($number);    }    public function getValueByNumber($number)    {        return parent::getValueByNumber($number);    }    public function getDate($name)    {        $date = null;        try {            $date = (new ExcelDate())->getDate($this->getValue($name));        } catch (\Exception $exception) {            (new Debug())->write($exception->getMessage());        }        return $date;    }    public function getDateByNumber($number)    {        $date = (new ExcelDate())->getDate($this->getValueByNumber($number));        return $date;    }}