<?phpnamespace Nemundo\Package\FontAwesome;use Nemundo\Html\Formatting\Italic;abstract class AbstractFontAwesomeIcon extends Italic{    /**     * @var string     */    protected $icon;    /**     * @var string     */    public $size;    public function getContent()    {        $this->checkProperty('icon');        //$this->addCssClass('fa-solid fa-' . $this->icon);        $this->addCssClass('fa-light fa-' . $this->icon);        //$this->addCssClass('fa-2xl');        if ($this->size !== null) {            $this->addCssClass('fa-2xl');        } else {            $this->addCssClass($this->size);        }        return parent::getContent();    }}