<?phpnamespace Nemundo\Package\FontAwesome;use Nemundo\Html\Formatting\Italic;abstract class AbstractFontAwesomeIcon extends Italic{    /**     * @var string     */    protected $icon;    /**     * @var string     */    public $size;    public function getContent()    {        $this->checkProperty('icon');        $this->addCssClass('fa-brands fa-' . $this->icon);        // nur Probrands        //$this->addCssClass('fa-thin fa-' . $this->icon);        //<i class="fa-thin fa-file-excel"></i>        //$this->addCssClass('fa-2xl');        if ($this->size !== null) {            $this->addCssClass('fa-2xl');        } else {            $this->addCssClass($this->size);        }        return parent::getContent();    }}