<?phpnamespace Nemundo\Package\FontAwesome;use Nemundo\Html\Formatting\Italic;abstract class AbstractFontAwesomeIcon extends Italic{    /**     * @var string     */    protected $icon;    /**     * @var string     */    public $size;    /**     * GrÃ¶sse zwischen 1 und 5     * @var int     */    //public $iconSize;    /**     * Farb Hex Code     * @var string     */    //public $color;    /**     * @var bool     */    //public $solid = false;    public function getContent()    {        $this->checkProperty('icon');        /*$size = '';        if ($this->iconSize !== null) {            $size = ' fa-' . $this->iconSize . 'x';        }*/        $this->addCssClass('fa-solid fa-' . $this->icon);        if ($this->size !== null) {            $this->addCssClass('fa-2xl');        }        return parent::getContent();    }}