<?phpnamespace Nemundo\Package\OpenGraph;use Nemundo\Html\Header\AbstractHeaderHtmlContainer;use Nemundo\Html\Header\Meta\Meta;abstract class AbstractOpenGraph extends AbstractHeaderHtmlContainer{    public $pageTitle;    public $pageDescription;    public $pageImage;    protected $prefix;    protected $propertyName = 'property';    protected function addMeta($property, $content)    {        if ($content !== null) {            $meta = new Meta($this);            //$meta->addAttribute('property', $this->prefix . ':' . $property);            $meta->addAttribute($this->propertyName, $this->prefix . ':' . $property);            $meta->addAttribute('content', $content);        }    }    public function getContent()    {        $this->addMeta('title', $this->pageTitle);        $this->addMeta('description', $this->pageDescription);        $this->addMeta('image', $this->pageImage);        return parent::getContent();    }}