<?phpnamespace Nemundo\Package\OpenGraph;use Nemundo\Html\Header\AbstractHeaderHtmlContainer;use Nemundo\Com\Html\Header\LibraryHeader;use Nemundo\Html\Header\Meta\Meta;use Nemundo\Web\ResponseConfig;abstract class AbstractOpenGraph extends AbstractHeaderHtmlContainer{    public $pageTitle;    public $pageDescription;    public $pageImage;    protected $prefix;    protected function addMeta($property, $content)    {        if ($content !== null) {            $meta = new Meta($this);            $meta->addAttribute('property', $this->prefix . ':' . $property);            $meta->addAttribute('content', $content);        }    }    public function getContent()    {        //$this->prefix = 'og';        $this->addMeta('title', $this->pageTitle);  // LibraryHeader::$documentTitle); // $this->title);        $this->addMeta('description', $this->pageDescription);  // ResponseConfig::$description);        $this->addMeta('image', $this->pageImage);  // ResponseConfig::$imageUrl);        return parent::getContent();    }}