<?phpnamespace Nemundo\Project\Config;use Nemundo\Core\Base\AbstractBaseClass;use Nemundo\Core\Config\ConfigWriter;use Nemundo\Core\File\File;use Nemundo\Core\Type\Number\YesNo;class ProjectConfigBuilder extends AbstractBaseClass{    /**     * @var string     */    public $filename;    public $mysqlHost = 'localhost';    public $mysqlPort = 3306;    public $mysqlUser = 'root';    public $mysqlPassword;    public $mysqlDatabase;    public $webUrl = '/';    //buildConfigFile    public function writeConfigFile()    {        $file = new File($this->filename);        if (!$file->fileExists()) {            $writer = new ConfigWriter($this->filename);            $writer->overwriteExistingFile = true;            $writer->add('mysql_host', $this->mysqlHost);            $writer->add('mysql_port', $this->mysqlPort);            $writer->add('mysql_user', $this->mysqlUser);            $writer->add('mysql_password', $this->mysqlPassword);            $writer->add('mysql_database', $this->mysqlDatabase);            $writer->add('web_url', $this->webUrl);            $writer->writeFile();        }    }}