<?phpnamespace Nemundo\Project\Deployment\Copy;use Nemundo\Core\Base\AbstractBase;use Nemundo\Core\File\DirectoryCopy;use Nemundo\Core\Path\Path;use Nemundo\Core\Repository\AbstractRepository;use Nemundo\Core\Type\Text\Text;class ProjectCopy extends AbstractBase{    /**     * @var Path     */    public $path;    public function copyProject(AbstractRepository $project)    {        $copy = new DirectoryCopy();        //$copy->overwriteExistingFile = true;        $copy->sourcePath = (new Path($project->path))            ->addParentPath()            ->getPath();        $copy->destinationPath = $this->path->getPath();        $copy->copyDirectory();        return $this;    }}