<?phpnamespace Nemundo\Project\Deployment\Git;use Nemundo\App\Git\Command\GitCloneCommand;use Nemundo\App\Git\Command\GitCommit;use Nemundo\Core\Debug\Debug;use Nemundo\Project\Deployment\AbstractDeployment;abstract class AbstractGitDeployment extends AbstractDeployment{    public $deploymentPath;    public $deploymentGitUrl;    public function __construct()    {        $this->createConfigFile = false;        parent::__construct();    }    public function deploy()    {        if (!$this->getDeploymentPath()->existPath()) {            $gitClone = new GitCloneCommand();            $gitClone->gitUrl = $this->deploymentGitUrl;            $gitClone->path = $this->getDeploymentPath()->getPath();            $gitClone->runCommand();        }        parent::deploy();        $this->onBeforeGitCommitPush();        $commit = new GitCommit();        $commit->path = $this->getDeploymentPath()->getPath();        $output = $commit->runCommand();        /*$push = new GitCommitPush();        $push->path = $this->getDeploymentPath()->getPath();        $output = $push->runCommand();*/        (new Debug())->write($output);        (new Debug())->write('Git Url:');        (new Debug())->write('git clone ' . $this->deploymentGitUrl . ' .');    }    protected function onBeforeGitCommitPush()    {    }}