<?phpnamespace Nemundo\Project\Deployment\Git;use Nemundo\Project\Deployment\DeploymentConfig;abstract class AbstractGithubDeployment extends AbstractGitDeployment{    protected $githubShortUrl;    public function __construct()    {        $this->createConfigFile = false;        parent::__construct();        $this->deploymentGitUrl = 'https://' . DeploymentConfig::$token . '@github.com/' . $this->githubShortUrl;        $this->deploymentPath = DeploymentConfig::$deploymentPath . $this->project->projectName;    }    public function getGithubUrl() {        return $this->deploymentGitUrl;    }}