<?phpnamespace Nemundo\Project\Path;use Nemundo\Core\Log\LogMessage;use Nemundo\Core\Path\AbstractPath;use Nemundo\Project\Config\ProjectConfigReader;use Nemundo\Project\ProjectConfig;class CachePath extends AbstractPath{    /**     * @var string     */    private static $cachePath;    protected function loadPath()    {        if (CachePath::$cachePath == null) {            CachePath::$cachePath = (new ProjectConfigReader())->getValue('cache_path');            if (CachePath::$cachePath == '') {                //(new LogMessage())->writeError('Cache Path not defined');                CachePath::$cachePath = (new ProjectPath())                    ->addPath('cache')                    ->getPath();                /*$this                    ->addPath(ProjectConfig::$projectPath)                    ->addPath('cache');*/            }        }        $this->addPath(CachePath::$cachePath);    }}