<?phpnamespace Nemundo\Project\Script;use Nemundo\App\Script\Type\AbstractConsoleScript;use Nemundo\Project\Clean\ProjectClean;use Nemundo\Project\Path\LogPath;use Nemundo\Project\Path\TmpPath;class ProjectCleanScript extends AbstractConsoleScript{    protected function loadScript()    {        $this->scriptName = 'project-clean';    }    public function run()    {        (new TmpPath())            ->emptyDirectory();        (new LogPath())            ->emptyDirectory();        (new ProjectClean())            ->cleanProject();    }}