<?phpnamespace Nemundo\Svg;use Nemundo\Html\Container\AbstractHtmlContainer;use Nemundo\Svg\Color\RgbColor;class Circle extends AbstractHtmlContainer{    /**     * @var int     */    public $x;    /**     * @var int     */    public $y;    /**     * @var int     */    public $radius;    /**     * @var Color     */    public $color;    /**     * @var RgbColor     */    public $rgbColor;    public function getContent()    {        $this->tagName = 'circle';        $this->renderClosingTag=false;        $this->addAttribute('cx', $this->x);        $this->addAttribute('cy', $this->y);        $this->addAttribute('r', $this->radius);        $this->addAttribute('fill', $this->color);        if ($this->rgbColor!==null) {        //$this->addAttribute('style','color:rgb(155,155,155);');            //$text= 'rgb('.$this->rgbColor->r.','.$this->rgbColor->g.','.$this->rgbColor->b.')';            $text = 'hsl(14, 100%, '.$this->rgbColor->hsl.'%)';//fill = "rgb(240, 200, 255)"            $this->addAttribute('fill', $text);            //fill = "rgb(240, 200, 255)"        }        //<circle cx="50" cy="50" r="40" stroke="green" stroke-width="4" fill="yellow" />        return parent::getContent();    }}