<?phpnamespace Nemundo\Svg;use Nemundo\Html\Container\AbstractTagContainer;use Nemundo\Html\Document\AbstractDocument;class SvgImage extends AbstractTagContainer{    public $width=500;    public $height=500;    public function getContent()    {        $this->tagName='svg';        $this->addAttribute('width',$this->width);        $this->addAttribute('height',$this->height);        $this->addContent('Browser does not support inline SVG');        return parent::getContent();    }}