<?phpnamespace Nemundo\User\Com\Form;use Nemundo\Admin\Com\Form\AbstractAdminForm;use Nemundo\User\Com\ListBox\UserListBox;use Nemundo\User\Data\UserUsergroup\UserUsergroup;class UserUsergroupForm extends AbstractAdminForm{    public $usergroupId;    /**     * @var UserListBox     */    private $user;    public function getContent()    {        $this->user = new UserListBox($this);        $this->user->validation = true;        return parent::getContent();    }    protected function onSubmit()    {        $data = new UserUsergroup();        $data->ignoreIfExists = true;        $data->userId = $this->user->getValue();        $data->usergroupId = $this->usergroupId;        $data->save();    }}