<?phpnamespace Nemundo\User\Com\ListBox;use Nemundo\Admin\Com\ListBox\AdminListBox;use Nemundo\Core\Language\LanguageCode;use Nemundo\User\Parameter\UserParameter;use Nemundo\User\Reader\User\UserDataReader;class UserListBox extends AdminListBox{    protected function loadContainer()    {        parent::loadContainer();        $this->label[LanguageCode::EN] = 'User';        $this->label[LanguageCode::DE] = 'Benutzer';        $this->name = (new UserParameter())->getParameterName();    }    public function getContent()    {        $userReader = new UserDataReader();        //$userReader->filter->andEqual($userReader->model->active, true);        //$userReader->addOrder($userReader->model->displayName);        foreach ($userReader->getData() as $userRow) {            //$this->addItem($userRow->id, $userRow->login);            $this->addItem($userRow->id, $userRow->displayName);        }        return parent::getContent();    }}