<?phpnamespace Nemundo\User\Install;use Nemundo\App\Application\Type\Install\AbstractInstall;use Nemundo\App\Backup\Application\BackupApplication;use Nemundo\App\Backup\Setup\BackupSetup;use Nemundo\App\Script\Setup\ScriptSetup;use Nemundo\Model\Setup\ModelCollectionSetup;use Nemundo\User\Application\UserApplication;use Nemundo\User\Backup\UserBackup;use Nemundo\User\Data\UserModelCollection;use Nemundo\User\Script\AdminUserScript;use Nemundo\User\Script\UsergroupCleanScript;class UserInstall extends AbstractInstall{    public function install()    {        (new BackupApplication())->installApp();        (new ModelCollectionSetup())            ->addCollection(new UserModelCollection());        (new ScriptSetup(new UserApplication()))            ->addScript(new AdminUserScript())            ->addScript(new UsergroupCleanScript());        (new BackupSetup(new UserApplication()))            ->addBackup(new UserBackup());    }}