<?phpnamespace Nemundo\User\Password;use Nemundo\User\Builder\AbstractUserBuilder;use Nemundo\User\Data\User\UserUpdate;// ChangePassword// PasswordReset// PasswordUserBuilder// PasswordBuilderclass PasswordChange extends AbstractUserBuilder  // AbstractBaseClass{    /**     * @var string     */    public $login;    /**     * @var string     */    public $password;    public function changePassword($passwordNew)    {        $this->changePasswordByHash((new PasswordHash($passwordNew))->getHashPassword());        return $this;    }    public function changePasswordByHash($passwordHash)    {        if ($passwordHash !== null) {            $update = new UserUpdate();            $update->password = $passwordHash;            $update->updateById($this->userId);        }        return $this;    }}