<?phpnamespace Nemundo\User\Reader\User;use Nemundo\User\Data\User\UserRow;use Nemundo\User\Data\Usergroup\UsergroupRow;use Nemundo\User\Data\UserUsergroup\UserUsergroupReader;class UserDataRow extends UserRow{    public function getUsergroupList()    {        /** @var UsergroupRow[] $list */        $list = [];        $userUsergroupReader = new UserUsergroupReader();        $userUsergroupReader->model->loadUsergroup();        $userUsergroupReader->filter->andEqual($userUsergroupReader->model->userId, $this->id);        foreach ($userUsergroupReader->getData() as $userUsergroupRow) {            $list[] = $userUsergroupRow->usergroup;        }        return $list;    }}