<?phpnamespace Nemundo\User\Script;use Nemundo\App\Script\Type\AbstractConsoleScript;use Nemundo\Core\Console\ConsoleInput;use Nemundo\User\Builder\UserBuilder;use Nemundo\User\Builder\UserUsergroupBuilder;use Nemundo\User\Password\PasswordChange;class AdminUserScript extends AbstractConsoleScript{    protected function loadScript()    {        $this->scriptName = 'admin-user';    }    public function run()    {        $input = new ConsoleInput();        $input->message = 'Password';        $password = $input->getValue();        $user = new UserBuilder();        $user->login = 'admin';        $user->email = 'noreply@noreply.com';        $userId = $user->createUser();        $builder = new PasswordChange($userId);        $builder->changePassword($password);        (new UserUsergroupBuilder($userId))->addAllUsergroup();    }}