<?phpnamespace Nemundo\Web\Page;use Nemundo\Html\Document\HtmlDocument;use Nemundo\Html\Header\Title;use Nemundo\Html\Paragraph\Paragraph;class NotFoundPage extends HtmlDocument{    public function getContent()    {        $text = '404 - Page not found';        $title = new Title($this);        $title->content = $text;        $p = new Paragraph($this);        $p->content = $text;        return parent::getContent();    }}